import * as React from "react"
import { HTMLMotionProps, motion } from "framer-motion"
import { ArrowRight } from "lucide-react"

import { cn } from "@/lib/utils"

interface ButtonProps extends HTMLMotionProps<"button"> {
  children?: React.ReactNode
  className?: string
  headerText: string
  bodyText: string
}

function CTButton({ className, headerText, bodyText, ...props }: ButtonProps) {
  return (
    <motion.button data-slot="button" className={cn("", className)} {...props}>
      <div className="relative flex flex-shrink-0 flex-grow-0 flex-col items-center justify-start gap-2">
        <p className="w-60 flex-shrink-0 flex-grow-0 text-left text-sm font-medium text-black/20">
          {headerText}
        </p>
        <p className="h-9 w-60 flex-shrink-0 flex-grow-0 text-left text-sm text-[#545454]">
          {bodyText}
        </p>
      </div>
      <ArrowRight className="size-8" />
    </motion.button>
  )
}

export { CTButton }
